import React,{ useEffect} from "react";
import { useSelector, useDispatch } from 'react-redux';
import { getMission,missionUpdate } from "../Redux/mission/mission";
import "../Styles/Mission.css"

const Mission = () => {
  const dispatch= useDispatch()
  useEffect(()=>{
     dispatch(getMission())
  },[dispatch])
  const { missions}= useSelector((store)=>store.mission)

  const updateMission= (id)=>{
  const dene= missions.map((item)=>{
    if (item.id=== id){
      const missionState=item.mission
      return{
        ...item,
        mission:!missionState
      }
    }
    return item
  })
  dispatch(missionUpdate(missionUpdate(dene)))
  }

  return (
    <div className="mission-page">
      <table>
        <thead>
          <tr>
            <th>Mission</th>
            <th>Description</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {missions.map(({id,name,description,mission}) => (
            <tr key={id} className="mission-card">
              <td>{ name }</td>
              <td>{ description }</td>
              <td className="mission-btns">
                <p className="rocket-btn">{mission? ' active Member' : 'Not a Memeber'}</p>
                <button type="button" onClick={()=>updateMission(id)} className="rocket-btn">{ mission? 'Cancel Misson ' : ' Join Mission'}</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Mission;
