import React,{ useEffect} from "react";
import { useSelector, useDispatch } from 'react-redux';
import { getMission } from "../Redux/mission/mission";
import "../Styles/Mission.css"

const Mission = () => {
  const dispatch= useDispatch()
  useEffect(()=>{
     dispatch(getMission())
  },[dispatch])
